import React from 'react'
/**
 * Updates every second
 */
export default class Clock extends React.Component {
constructor(props) {
    super(props);
        this.state = {date: new Date()};
    }
    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(),
            1000
        );
    }
    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    tick() {
        this.setState({
            date: new Date()
        });
    }
    render() {
        if (this.props.timezone != "Europe/Stockholm") {
            return (
                <div>
                    <h2>{this.state.date.toLocaleTimeString('en-GB', {timeZone: this.props.timezone})}</h2>
                </div>
            );
        } else {
            return (
                <div>
                   !!!! {this.props.timeZone} <h2>{this.state.date.toLocaleTimeString('en-GB', {timeZone: this.props.timezone})}</h2>
                </div>
            );
        }
    }
}
