import React from 'react'
/**
 * Updates every minute
 */
export default class DateViewer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date()};
        this.weekDay = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
        this.month = ["January", "February", "March", "April", "May", "June", "July", "August", 
                      "September", "October", "November", "December"];
    }
    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(),
            60 * 1000
        );
    }
    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    tick() {
        this.setState({
            date: new Date()
        });
    }
    getWeekdayName() {
        return this.weekDay[this.state.date.getDay()];
    }
    getMonthName() {
        return this.weekDay[this.state.date.getMonth()];
    }
    getDateNumber() {
        return this.state.date.getDate();
    }
    render() {
        return (
            <div>
                <h4>{this.getWeekdayName()}, {this.getMonthName()} {this.getDateNumber()}.</h4>
            </div>
        );
    }
}
